import React, { useState } from 'react';
import { Box, TextField, Button, Typography } from '@mui/material';
import { motion } from 'framer-motion';

const MotionBox = motion(Box);

const ActivationPrompt = ({ onActivate }) => {
  const [key, setKey] = useState('');
  const [error, setError] = useState('');

  const handleActivate = () => {
    if (key === 'your-secret-key') {
      localStorage.setItem('emiCalculatorActivated', 'true');
      onActivate();
    } else {
      setError('Invalid activation key. Please try again.');
    }
  };

  return (
    <MotionBox
      sx={{
        maxWidth: 400,
        mx: 'auto',
        mt: 4,
        p: 3,
        borderRadius: 4,
        backgroundColor: 'white',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
      }}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <Typography variant="h5" gutterBottom color="primary" textAlign="center">
        Activate EMI Calculator
      </Typography>
      <TextField
        fullWidth
        label="Activation Key"
        value={key}
        onChange={(e) => setKey(e.target.value)}
        error={!!error}
        helperText={error}
        sx={{ mb: 2 }}
      />
      <Button color="secondary" onClick={handleActivate} fullWidth sx={{ py: 1.5 }}>
        Activate
      </Button>
    </MotionBox>
  );
};

export default ActivationPrompt;
