import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import InputAdornment from '@mui/material/InputAdornment';
import { motion } from 'framer-motion';
import ActivationPrompt from './ActivationPrompt';

const theme = createTheme({
  palette: {
    primary: {
      main: '#6200ea',
    },
    secondary: {
      main: '#03dac6',
    },
    background: {
      default: '#f5f5f5',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 700,
    },
  },
  components: {
    MuiTextField: {
      defaultProps: {
        variant: 'outlined',
      },
    },
    MuiButton: {
      defaultProps: {
        variant: 'contained',
      },
      styleOverrides: {
        root: {
          borderRadius: 8,
        },
      },
    },
  },
});

const MotionContainer = motion(Container);
const MotionBox = motion(Box);

function App() {
  const [isActivated, setIsActivated] = useState(false);
  const [loanAmount, setLoanAmount] = useState('');
  const [interestRate, setInterestRate] = useState('');
  const [loanTerm, setLoanTerm] = useState('');
  const [emi, setEmi] = useState(null);
  const [totalInterest, setTotalInterest] = useState(null);
  const [totalAmount, setTotalAmount] = useState(null);

  useEffect(() => {
    const activated = localStorage.getItem('emiCalculatorActivated');
    if (activated === 'true') {
      setIsActivated(true);
    }
  }, []);

  const calculateEMI = () => {
    const principal = parseFloat(loanAmount);
    const rate = parseFloat(interestRate) / 100 / 12;
    const time = parseFloat(loanTerm) * 12;

    if (principal > 0 && rate > 0 && time > 0) {
      const emiValue = (principal * rate * Math.pow(1 + rate, time)) / (Math.pow(1 + rate, time) - 1);
      const totalPayment = emiValue * time;
      const interestPayment = totalPayment - principal;

      setEmi(emiValue.toFixed(2));
      setTotalInterest(interestPayment.toFixed(2));
      setTotalAmount(totalPayment.toFixed(2));
    }
  };

  const handleActivate = () => {
    setIsActivated(true);
  };

  if (!isActivated) {
    return (
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <MotionContainer
          maxWidth="sm"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <ActivationPrompt onActivate={handleActivate} />
        </MotionContainer>
      </ThemeProvider>
    );
  }

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <MotionContainer
        maxWidth="sm"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <MotionBox
          sx={{
            my: 4,
            p: 3,
            borderRadius: 4,
            backgroundColor: 'white',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
          }}
        >
          <Typography variant="h4" component="h1" gutterBottom color="primary" textAlign="center">
            EMI Calculator
          </Typography>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Loan Amount"
                type="number"
                value={loanAmount}
                onChange={(e) => setLoanAmount(e.target.value)}
                InputProps={{
                  startAdornment: <InputAdornment position="start">₹</InputAdornment>,
                }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Interest Rate"
                type="number"
                value={interestRate}
                onChange={(e) => setInterestRate(e.target.value)}
                InputProps={{
                  endAdornment: <InputAdornment position="end">%</InputAdornment>,
                }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Loan Term (Years)"
                type="number"
                value={loanTerm}
                onChange={(e) => setLoanTerm(e.target.value)}
              />
            </Grid>
            <Grid item xs={12}>
              <Button
                color="secondary"
                onClick={calculateEMI}
                fullWidth
                sx={{ py: 1.5 }}
              >
                Calculate EMI
              </Button>
            </Grid>
          </Grid>
          {emi && (
            <MotionBox
              sx={{ mt: 4 }}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <Typography variant="h6" gutterBottom color="primary">
                Results:
              </Typography>
              <Typography variant="body1" gutterBottom>
                Monthly EMI: <strong>₹{emi}</strong>
              </Typography>
              <Typography variant="body1" gutterBottom>
                Total Interest: <strong>₹{totalInterest}</strong>
              </Typography>
              <Typography variant="body1" gutterBottom>
                Total Amount: <strong>₹{totalAmount}</strong>
              </Typography>
            </MotionBox>
          )}
        </MotionBox>
      </MotionContainer>
    </ThemeProvider>
  );
}

export default App;
